$(function(){$("form").submit(function(){return $("span.error").fadeOut(200),$.blockUI(),$(this).ajaxSubmit({url:this.action,dataType:"json",success:function(r){if($.unblockUI(),r.error)$.alert(r.error);else if(r.errors)for(var e in r.errors)$('span.error[name="'+e+'"]').html(r.errors[e]).fadeIn(200);else if(r.added){var a=$('input[name="back"]').val();document.location.href=a}else if(r.saved){var n=$('input[name="login"]').val();$(".path > .part > span").html(n)}},error:function(r){$.unblockUI(),$.alert(r.statusText)}}),!1})});